                  <input 
                    ref={timeRef}
                    type="text" 
                    name="displayTime"
                    value={bookingDetails.bookingTime ? formatTime(bookingDetails.bookingTime) : ""}
                    placeholder="Select Pickup Time"
                    className="bg-transparent w-full focus:outline-none cursor-pointer z-10"
                    onClick={toggleTimePicker}
                    readOnly
                    required
                  />

                  {/* Custom Time Picker Dropdown */}
                  {showTimePicker && (
                    <div className="absolute top-full left-0 mt-2 w-full bg-white shadow-xl rounded-lg border border-gray-200 z-50 max-h-64 overflow-hidden">
