                        <div className="text-center font-medium mb-2 text-gray-700">Select Time</div>
                        <div className="max-h-48 overflow-y-auto py-1 px-1 space-y-1">
                          {generateTimeOptions().map((time, index) => (
                            <div 
                              key={index} 
                              className={`py-2 px-3 hover:bg-yellow-50 cursor-pointer rounded transition-colors ${
                                bookingDetails.bookingTime === time.value ? 'bg-yellow-100 font-medium' : ''
                              }`}
                              onClick={() => handleTimeSelection(time)}
                            >
                              {time.display}
                            </div>
                          ))}
                        </div>
                      </div>
                    </div>
                  )}
                </div>
                <ChevronDown 
                  className="w-5 h-5 text-gray-500 cursor-pointer" 
                  onClick={toggleTimePicker}
                />
