                </button>
                <button 
                  type="submit"
                  disabled={emailStatus.sending}
                  className={`w-full py-4 rounded-lg transition-colors font-medium ${
                    emailStatus.sending 
                      ? 'bg-gray-300 text-gray-600' 
                      : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
                  }`}
                >
                  {emailStatus.sending ? 'Sending...' : 'No Whatsapp...?'}
                </button>
              </div>
            </form>
          </div>
        </div>
