              <Mail className="w-6 h-6" />
              <span>shafeer@example.com</span>
            </a>
            <div className="flex items-center space-x-2">
              <MapPin className="w-6 h-6 text-yellow-400" />
              <span>Dubai, United Arab Emirates</span>
            </div>
          </div>
        </div>
